#!/usr/bin/env python3

## check GAATTC
dna = ''

start = dna.find('GAATTC')
end = start + 6
site = dna[start:end]

report = """
site {}
EcoRI startPos:{} endPos:{}
"""
print(report.format(site, start + 1 , end + 1))

## check complementary sequence of CTTAA/G 
rev_dna = dna[::-1]
if 'CTTAAG' in rev_dna:

    start = rev_dna.find('CTTAAG')
    end = start + 6
    site = rev_dna[start:end]

    print("site: ", site)

    report = """
complementary site {}
EcoRI startPos:{} endPos:{}
    """
    print(report.format(site, start + 1 , end + 1))
