#!/usr/bin/env python3


dna = ''
dna_total = len(dna)

at_content = dna.upper().count('A') + dna.upper().count('T')
gc_content = dna.upper().count('G') + dna.upper().count('C')

at_report = "the AT content is {:0.2f}%"
cg_report = "the GC content is {:0.2f}%"

print(at_report.format((at_content/dna_total)* 100))
print(cg_report.format((gc_content/dna_total)* 100))
