#!/usr/bin/env python3

dna = ''

dna_set = set(dna)
print("DNA unique characters: ", dna_set)

dna_count = {}
for i in dna_set:
    dna_count[i] = dna.count(i)

total = 0
gc = 0
for seq, count in dna_count.items():
    total = total + count
    if seq == 'C' or seq == 'G':
        gc = gc + count

gc_content = (gc / total) * 100

report = "The GC content of your dna is: {:0.2f}%"
print(report.format(gc_content))
